import React, { Component } from 'react'
import Web3 from 'web3'
import AdaToken from '../abis/DaiToken.json'
import RewardTokenToken from '../abis/DappToken.json'
import TokenFarm from '../abis/TokenFarm.json'
import Navbar from './Navbar'
import Main from './Main'
import './App.css'

class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    const networkId = await web3.eth.net.getId()

    // Load AdaToken
    const AdaTokenData = AdaToken.networks[networkId]
    if(AdaTokenData) {
      const AdaToken = new web3.eth.Contract(AdaToken.abi, AdaTokenData.address)
      this.setState({ AdaToken })
      let AdaTokenBalance = await AdaToken.methods.balanceOf(this.state.account).call()
      this.setState({ AdaTokenBalance: AdaTokenBalance.toString() })
    } else {
      window.alert('AdaToken contract not deployed to detected network.')
    }

    // Load RewardTokenToken
    const RewardTokenTokenData = RewardTokenToken.networks[networkId]
    if(RewardTokenTokenData) {
      const RewardTokenToken = new web3.eth.Contract(RewardTokenToken.abi, RewardTokenTokenData.address)
      this.setState({ RewardTokenToken })
      let RewardTokenTokenBalance = await RewardTokenToken.methods.balanceOf(this.state.account).call()
      this.setState({ RewardTokenTokenBalance: RewardTokenTokenBalance.toString() })
    } else {
      window.alert('RewardTokenToken contract not deployed to detected network.')
    }

    // Load TokenFarm
    const tokenFarmData = TokenFarm.networks[networkId]
    if(tokenFarmData) {
      const tokenFarm = new web3.eth.Contract(TokenFarm.abi, tokenFarmData.address)
      this.setState({ tokenFarm })
      let stakingBalance = await tokenFarm.methods.stakingBalance(this.state.account).call()
      this.setState({ stakingBalance: stakingBalance.toString() })
    } else {
      window.alert('TokenFarm contract not deployed to detected network.')
    }

    this.setState({ loading: false })
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  stakeTokens = (amount) => {
    this.setState({ loading: true })
    this.state.AdaToken.methods.approve(this.state.tokenFarm._address, amount).send({ from: this.state.account }).on('transactionHash', (hash) => {
      this.state.tokenFarm.methods.stakeTokens(amount).send({ from: this.state.account }).on('transactionHash', (hash) => {
        // this.setState({ loading: false })
      })
    })
  }

  unstakeTokens = (amount) => {
    this.setState({ loading: true })
    this.state.tokenFarm.methods.unstakeTokens().send({ from: this.state.account }).on('transactionHash', (hash) => {
      this.setState({ loading: false })
    })
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '0x0',
      AdaToken: {},
      RewardTokenToken: {},
      tokenFarm: {},
      AdaTokenBalance: '0',
      RewardTokenTokenBalance: '0',
      stakingBalance: '0',
      loading: true
    }
  }

  render() {
    let content
    if(this.state.loading) {
      content = <p id="loader" className="text-center">Loading...</p>
    } else {
      content = <Main
        AdaTokenBalance={this.state.AdaTokenBalance}
        RewardTokenTokenBalance={this.state.RewardTokenTokenBalance}
        stakingBalance={this.state.stakingBalance}
        stakeTokens={this.stakeTokens}
        unstakeTokens={this.unstakeTokens}
      />
    }

    return (
      <div>
        <Navbar account={this.state.account} />
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '600px' }}>
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.RewardTokenuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                </a>

                {content}
                Hello

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
